INSERT INTO bustravelagency.tours (id_Tour, Title, Price, Type, Hot_tour, Number_of_places, Date_start, Date_end, id_Discount, id_Hotel, Description, Url_wallpaper) VALUES (1020, 'Выходные в Стокгольме', 250.00, 1, 0, 25, '17-02-2021', '20-02-2021', 5, 7878, 'В 10:30 прибытие в Стокгольм Стокгольм – коллаж впечатлений, встреча с авиагруппой, далее совместно.
